<template>
  <div class="container">
    <div class="mt-5 text-light ">
      <img src="../assets/bonovie_lg.png" style="width: 900px;" class="img-fluid">
    </div>
    <SearchForm />
      <div class="row row-cols-auto d-flex justify-content-center">
        <MovieListItem 
        v-for="movie in movies"
        :key="movie.id"
        :movie="movie"
        />
      </div>
      <div id="more" class="my-5" @click="addlimit">
        <img  class="more-img" src="data:image/jpeg;base64," style="width: 50px; height: 50px;">
        <p class="text-light">More</p>
      </div>
  </div>
</template>

<script>
import SearchForm from '@/components/home/SearchForm'
import MovieListItem from '@/components/home/MovieListItem'
import { mapActions, mapGetters } from 'vuex'

export default {
  name: 'HomeView',
  components: {
    SearchForm,
    MovieListItem, 
  },
  data() {
    return {
      limit: 50
    }
  },
  computed: {
    ...mapGetters(['movies'])
  },
  methods: {
    ...mapActions(['fetchMovies']),
    addlimit() {
      this.limit = this.limit + 50
      this.fetchMovies(this.limit)
    }
  },
  created() {
    this.fetchMovies(this.limit)
  }

}
</script>

<style scoped>
.more-img {
  border: none;
  border-radius: 100%;
}

#more :hover {
  cursor: pointer;
  transform: scale(1.1);
  transition: 100ms ease-in;
}
</style>